// poker-helper.js /** æª¢æŸ¥æ˜¯å¦é‚„æœ‰æ•ˆçš„ç§»å‹•å¡ç‰Œ * @param {CardStacks} cardStacks * @returns {boolean} æœ‰æœ‰æ•ˆç§»å‹•ç‚ºtrue å¯èƒ½æ²’æœ‰ç‚ºfalse */ function checkValidMove(cardStacks) { const dealerStacksValues = cardStacks[&#39;dealerStacks&#39;].map((card) =&gt; card.value); const seventLastValues = {}; // 1. `ä¸ƒç‰Œå †`æœ€å¾Œä¸€å¼µå¾Œé¢è¦æ¥çš„ç‰Œå­˜åœ¨æ–¼`ç™¼ç‰Œå€`ä¸­ let haveMove1 = SEVEN_STACKS.some((name) =&gt; { const stack = cardStacks[name]; if (stack.length === 0) { return false; } const lastCard = stack[stack.length - 1]; const lastCardNumber = lastCard.value % 13; seventLastValues[name] = lastCard.value; // Aå¾Œé¢æ²’æœ‰è¦æ¥çš„ï¼Œä½†å¯ç§»å‹•åˆ°çµç®—ç‰Œå †(å›æ‡‰æˆåŠŸ) if (lastCardNumber === 0) return true; const targetPokerValues = getDifferentColorPokerValues(lastCard.value - 1); return dealerStacksValues.some((value) =&gt; { return targetPokerValues.includes(value); }); }); if (haveMove1) { console.log(&#34;ç¬¬1æ´»å±€: `ä¸ƒç‰Œå †`æœ€å¾Œä¸€å¼µå¾Œé¢è¦æ¥çš„ç‰Œå­˜åœ¨æ–¼`ç™¼ç‰Œå€`&#34;); return true; } // 2. ä»»ä¸€`ä¸ƒç‰Œå †`å£“åœ¨éš±è—ç‰Œä¸Šæ”¾çš„é‚£å¼µå¯ä»¥æ¥åœ¨å…¶ä»–`ä¸ƒç‰Œå †`çš„å¾Œé¢(ä¸åŒ…å«æœ¬èº«ç‰Œå †) let haveMove2 = SEVEN_STACKS.some((name) =&gt; { const stack = cardStacks[name]; if (stack.length === 0) { return false; } let firstOpenCard = null; for (let i = 1; i &lt; stack.length; i++) { if (stack[i].isOpen &amp;&amp; (!stack[i - 1].isOpen)) { firstOpenCard = stack[i]; break; } } // æ²’æœ‰å£“åœ¨éš±è—ç‰Œä¸Šçš„ç‰Œæˆ– æ”¾çš„é‚£å¼µæ˜¯Kç„¡æ³•æ¥åœ¨å…¶ä»–ç‰Œå †å¾Œé¢(å›æ‡‰å¤±æ•—) if (firstOpenCard === null || firstOpenCard.value % 13 === 12) { return false; } // æª¢æŸ¥æ˜¯å¦æœ‰å¯ä»¥æ¥çš„ç‰Œ const targetPokerValues = getDifferentColorPokerValues(firstOpenCard.value + 1) return SEVEN_STACKS.some((name2) =&gt; { if (name === name2) return false; if (seventLastValues[name2]) { return targetPokerValues.includes(seventLastValues[name2]); } return false; }); }); if (haveMove2) { console.log(&#34;ç¬¬2æ´»å±€: ä»»ä¸€`ä¸ƒç‰Œå †`å£“åœ¨éš±è—ç‰Œä¸Šæ”¾çš„é‚£å¼µå¯ä»¥æ¥åœ¨å…¶ä»–`ä¸ƒç‰Œå †`çš„å¾Œé¢(ä¸åŒ…å«æœ¬èº«ç‰Œå †)&#34;); return true; } // 3. `ç™¼ç‰Œå€`ä»»ä¸€å¼µç‰Œæˆ–`ä¸ƒç‰Œå †`çš„æœ€å¾Œä¸€å¼µ å¯ç§»å‹•è‡³ `çµç®—ç‰Œå †` let haveMove3 = FOUR_SUITS.some((suit, index) =&gt; { const stackLen = cardStacks[suit].length; if (stackLen === 13) return false; let targetValue = index * 13 + stackLen; return dealerStacksValues.includes(targetValue) || Object.values(seventLastValues).includes(targetValue); }); if (haveMove3) { console.log(&#34;ç¬¬3æ´»å±€: `ç™¼ç‰Œå€`ä»»ä¸€å¼µç‰Œæˆ–`ä¸ƒç‰Œå †`çš„æœ€å¾Œä¸€å¼µ å¯ç§»å‹•è‡³ `çµç®—ç‰Œå †`&#34;); } return haveMove3; } /** å–å¾—ç›¸åŒæ•¸å­—ä¸åŒé¡è‰²çš„æ’²å…‹ç‰Œç·¨ç¢¼ * @param {Number} pokerValue å°æ‡‰æ’²å…‹ç‰Œçš„ç·¨è™Ÿ * @returns {Array} [number1, number2] */ function getDifferentColorPokerValues(pokerValue) { const red = (Math.floor(pokerValue / 13) % 3) == 0 const number = pokerValue % 13; return [number + (red ? 13 : 0), number + (red ? 26 : 39)]; } ç„¶å¾Œåœ¨DragDemo.vueä¸­ä½¿ç”¨å‡½æ•¸checkValidMove(cardStacks)å°‡çµæœã€æ˜¯å¦æœ‰å¯ç§»å‹•çš„ç‰Œ(æ¨æ¸¬)ã€‘å„²å­˜åœ¨è¨ˆç®—refè®Šæ•¸ã€‚
