// DragDemo.vue /** * è‡ªå‹•ç§»å‹• * @param {String} fromName ä¾†è‡ªçš„ç‰Œå †åç¨± * @param {Card} card æƒ³ç§»å‹•çš„ç‰Œ */ function clickAutoMove(fromName, card) { const toNames = findFollowDeckName(cardStacks, card).sort((a, b) =&gt; { const aOrder = a.length + FOUR_SUITS.includes(a) ? -100 : 0; const bOrder = b.length + FOUR_SUITS.includes(b) ? -100 : 0; return aOrder - bOrder; }) // å¦‚æœæ²’æ‰¾åˆ°å°æ‡‰ç‰Œå †ï¼Œå‰‡ä¸åŸ·è¡Œ if (toNames.length == 0) { console.log(`å¡ç‰Œ${PokerValuesMap[card.value].content}æ²’æœ‰ç¬¦åˆç§»å‹•çš„è¦å‰‡`); return; } const toName = toNames[0]; const isToFinishedArea = FOUR_SUITS.includes(toName); if (fromName == &#39;dealerStacks&#39;) { // ä¾†è‡ª`ç™¼ç‰Œå †` const fromIndex = cardStacks[fromName].findIndex(c =&gt; c.value == card.value); const newFromCards = [ ...cardStacks[fromName].slice(0, fromIndex), ...cardStacks[fromName].slice(fromIndex + 1) ]; const newToCards = [ ...cardStacks[toName], cardStacks[fromName][fromIndex] ]; cardStacks[fromName] = newFromCards; cardStacks[toName] = newToCards; gameScore.value += isToFinishedArea ? 25 : 10; } else if (SEVEN_STACKS.includes(fromName)) { // ä¾†è‡ª7ç‰Œå † const fromLength = cardStacks[fromName].length; const fromIndex = cardStacks[fromName].findIndex(c =&gt; c.value == card.value); if (isToFinishedArea) { if (fromIndex != fromLength - 1) { console.log(`å¡ç‰Œ${PokerValuesMap[card.value].content}ä¸æ˜¯${fromName}çš„æœ€å¾Œä¸€å¼µç‰Œï¼Œä¸å¯ç§»å…¥çµç®—ç‰Œå †`); return; } const newFromCards = cardStacks[fromName].slice(0, fromIndex); const newToCards = [ ...cardStacks[toName], card ]; cardStacks[fromName] = newFromCards; cardStacks[toName] = newToCards; gameScore.value += 15; } else { const newFromCards = cardStacks[fromName].slice(0, fromIndex); const newToCards = [ ...cardStacks[toName], ...cardStacks[fromName].slice(fromIndex) ]; cardStacks[fromName] = newFromCards; cardStacks[toName] = newToCards; } } } å¦å¤–åœ¨poker-helper.jsæ–°å¢å‡½æ•¸findFollowDeckName(cardstacks, targetCard)ï¼Œä¸»è¦æ˜¯ç”¨ä¾†æ‰¾å‡ºtargetCardå¯ä»¥æ‹–æ›³åˆ°çš„ç‰Œå †åç¨±ï¼Œå› ç‚ºå¯èƒ½å¯ä»¥æ‹–æ›³åˆ°è¤‡æ•¸å€‹ç‰Œå †ï¼Œæ‰€ä»¥å›å‚³å­—ä¸²çµ„æˆçš„ä¸€ç¶­é™£åˆ—:
// utils/poker-helper.js /** * æ‰¾å‡º`æŒ‡å®šç‰Œ`å¯ä»¥æ¥åœ¨å“ªä¸€å€‹ç‰Œå †(7ç‰Œå †orçµç®—ç‰Œå †)çš„å¾Œé¢ * @param {CardStacks} cardstacks ç‰Œå † * @param {Card} targetCard æŒ‡å®šç‰Œ * @returns {Array&lt;String&gt;} Array&lt;ç›®æ¨™ç‰Œå †åç¨±&gt; */ function findFollowDeckName(cardstacks, targetCard) { const result = new Set(); // æ‰¾å‡ºå¯æ‹–æ›³è‡³7ç‰Œå †å°¾å·´çš„ç‰Œ SEVEN_STACKS.forEach((name) =&gt; { const stack = cardstacks[name]; if (stack.length === 0) { if ([12, 25, 38, 51].includes(targetCard.value)) { result.add(name); } return; } const lastCard = stack[stack.length - 1]; const lastCardNumber = lastCard.value % 13; const lastCardSymbol = Math.floor(lastCard.value / 13); // æª¢æŸ¥æ˜¯å¦ç‚ºAï¼Œå‰‡è·³é if (lastCardNumber === 0) { return; } const matchNumber = lastCardNumber - 1; const isBlack = lastCardSymbol % 3 == 0; const isInclude = [matchNumber + (isBlack ? 13 : 0), matchNumber + (isBlack ? 26 : 39)].includes(targetCard.value); if (isInclude) result.add(name); }); // æ‰¾å‡ºå¯æ‹–æ›³è‡³çµç®—ç‰Œå †å°¾å·´çš„ç‰Œ FOUR_SUITS.forEach((name, index) =&gt; { const stack = cardstacks[name]; if (stack.length === 0) { if (index * 13 == targetCard.value) result.add(name); return; } const lastCard = stack[stack.length - 1]; const lastCardNumber = lastCard.value % 13; // æª¢æŸ¥æ˜¯å¦ç‚ºKï¼Œå‰‡è·³é if (lastCardNumber === 12) { return; } const matchNumber = lastCardNumber + 1; if (matchNumber + index * 13 == targetCard.value) { result.add(name); } }); return Array.from(result); } å°çµ ä»Šå¤©å®Œæˆçš„åŠŸèƒ½é€£é»2ä¸‹è‡ªå‹•ç§»ç‰Œå¯ä»¥å–ä»£éƒ¨åˆ†æ‹–æ›³å¡ç‰Œçš„æ“ä½œï¼Œè®“éŠæˆ²æ›´åŠ é †æš¢ã€‚ é€™éƒ¨åˆ†ä¹Ÿæ˜¯åƒè€ƒæ›¾ç¶“ç©éçš„æ¥é¾éŠæˆ²åŠŸèƒ½ï¼Œç•¶æ™‚å°±è¦ºå¾—é€™æ¨£å¾ˆæ–¹ä¾¿ğŸ˜ã€‚
